import React, { useState, useEffect } from 'react';
import { Client } from '../../domain/entities/Client';
import { ClientController } from '../../application/controllers/ClientController';
import { ClientApiRepository } from '../../infrastructure/api/ClientApiRepository';
import '../styles/Views.css';

const ClientList: React.FC = () => {
  const [clients, setClients] = useState<Client[]>([]);
  const [selectedClient, setSelectedClient] = useState<Client | null>(null);
  const [isModalOpen, setIsModalOpen] = useState(false);
  const controller = new ClientController(new ClientApiRepository());

  useEffect(() => {
    loadClients();
  }, []);

  const loadClients = async () => {
    try {
      const data = await controller.getAllClients();
      setClients(data);
    } catch (error) {
      console.error('Error loading clients:', error);
    }
  };

  const handleCreate = async (client: Client) => {
    try {
      await controller.createClient(client);
      loadClients();
      setIsModalOpen(false);
    } catch (error) {
      console.error('Error creating client:', error);
    }
  };

  const handleUpdate = async (id: number, client: Client) => {
    try {
      await controller.updateClient(id, client);
      loadClients();
      setIsModalOpen(false);
    } catch (error) {
      console.error('Error updating client:', error);
    }
  };

  const handleDelete = async (id: number) => {
    try {
      await controller.deleteClient(id);
      loadClients();
    } catch (error) {
      console.error('Error deleting client:', error);
    }
  };

  const handleActivate = async (id: number) => {
    try {
      await controller.activateClient(id);
      loadClients();
    } catch (error) {
      console.error('Error activating client:', error);
    }
  };

  const handleDeactivate = async (id: number) => {
    try {
      await controller.deactivateClient(id);
      loadClients();
    } catch (error) {
      console.error('Error deactivating client:', error);
    }
  };

  return (
    <div className="view-container">
      <div className="view-header">
        <h1>Clientes</h1>
        <button 
          className="btn btn-primary"
          onClick={() => {
            setSelectedClient(null);
            setIsModalOpen(true);
          }}
        >
          Nuevo Cliente
        </button>
      </div>

      <table className="table">
        <thead>
          <tr>
            <th>Nombre</th>
            <th>Dirección</th>
            <th>Teléfono</th>
            <th>Estado</th>
            <th>Acciones</th>
          </tr>
        </thead>
        <tbody>
          {clients.map((client) => (
            <tr key={client.id}>
              <td>{client.fullName}</td>
              <td>{client.address}</td>
              <td>{client.phone}</td>
              <td>{client.status ? 'Activo' : 'Inactivo'}</td>
              <td>
                <button
                  className="btn btn-primary btn-sm"
                  onClick={() => {
                    setSelectedClient(client);
                    setIsModalOpen(true);
                  }}
                >
                  Editar
                </button>
                {client.status ? (
                  <button
                    className="btn btn-danger btn-sm"
                    onClick={() => handleDeactivate(client.id!)}
                  >
                    Desactivar
                  </button>
                ) : (
                  <button
                    className="btn btn-success btn-sm"
                    onClick={() => handleActivate(client.id!)}
                  >
                    Activar
                  </button>
                )}
                <button
                  className="btn btn-danger btn-sm"
                  onClick={() => handleDelete(client.id!)}
                >
                  Eliminar
                </button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>

      {isModalOpen && (
        <div className="modal">
          <div className="modal-content">
            <h2>{selectedClient ? 'Editar Cliente' : 'Nuevo Cliente'}</h2>
            <ClientForm
              client={selectedClient}
              onSubmit={(client) => {
                if (selectedClient) {
                  handleUpdate(selectedClient.id!, client);
                } else {
                  handleCreate(client);
                }
              }}
              onCancel={() => setIsModalOpen(false)}
            />
          </div>
        </div>
      )}
    </div>
  );
};

interface ClientFormProps {
  client?: Client | null;
  onSubmit: (client: Client) => void;
  onCancel: () => void;
}

const ClientForm: React.FC<ClientFormProps> = ({ client, onSubmit, onCancel }) => {
  const [formData, setFormData] = useState<Client>({
    fullName: client?.fullName || '',
    address: client?.address || '',
    phone: client?.phone || '',
    password: client?.password || '',
    status: client?.status ?? true,
  });

  const handleSubmit = (e: React.FormEvent) => {
    e.preventDefault();
    onSubmit(formData);
  };

  return (
    <form onSubmit={handleSubmit}>
      <div className="form-group">
        <label>Nombre completo:</label>
        <input
          type="text"
          className="form-control"
          value={formData.fullName}
          onChange={(e) =>
            setFormData({ ...formData, fullName: e.target.value })
          }
          required
        />
      </div>
      <div className="form-group">
        <label>Dirección:</label>
        <input
          type="text"
          className="form-control"
          value={formData.address}
          onChange={(e) =>
            setFormData({ ...formData, address: e.target.value })
          }
          required
        />
      </div>
      <div className="form-group">
        <label>Teléfono:</label>
        <input
          type="text"
          className="form-control"
          value={formData.phone}
          onChange={(e) =>
            setFormData({ ...formData, phone: e.target.value })
          }
          required
        />
      </div>
      <div className="form-group">
        <label>Contraseña:</label>
        <input
          type="password"
          className="form-control"
          value={formData.password}
          onChange={(e) =>
            setFormData({ ...formData, password: e.target.value })
          }
          required
        />
      </div>
      <div className="form-actions">
        <button type="submit" className="btn btn-primary">
          {client ? 'Actualizar' : 'Crear'}
        </button>
        <button type="button" className="btn btn-danger" onClick={onCancel}>
          Cancelar
        </button>
      </div>
    </form>
  );
};

export default ClientList;
